import React, { Component } from 'react'
import './css/reset.css'
import './App.css'

export default class App extends Component {

    constructor() {
        super()
        this.state = {
            srcs: [
                "./img/img (6).png",
                "./img/img (1).png",
                "./img/img (2).png"
            ]
        }
    }

    onTouchMove(event) {
        let touch = event.touches[0]
        // console.log(touch.clientX)
        console.log(event.target.parentNode.getComputedStyle('left'))
    }

    render() {
        return (
            <div className='App'>
                <ul className='Slide' onTouchMove={this.onTouchMove}>
                    {
                        this.state['srcs'].map((src, index) => {
                            return <li key={index}><img src={src} alt=''/></li>
                        })
                    }
                </ul>
            </div>
        )
    }
}
