import React, { Component } from 'react'
import './css/reset.css'
import './App.css'

export default class App extends Component {

    constructor() {
        super()
        this.state = {
            srcs: [5, 0, 1]
        };
        this.onTouchMove = this.onTouchMove.bind(this)
        this.onTouchStart = this.onTouchStart.bind(this)
        this.onTouchEnd = this.onTouchEnd.bind(this)
        this.moveLeft = this.moveLeft.bind(this)
        this.moveRight = this.moveRight.bind(this)

        // this.moveLeft()
        this.state.timer = setInterval(() => {
            this.moveLeft()
            this.setState()
            let srcs = this.state.srcs
            let newSrcs = []
            for (let i = 0; i < srcs.length; ++i) {
                newSrcs.push((srcs[i] + 1) % 6)
            }
            this.setState({ srcs: newSrcs })
            
        }, 2000)

        
    }

    onTouchStart(event) {
        this.setState({LastTouch:event.touches[0].clientX})
        this.setState({startTouch:event.touches[0].clientX})
    }

    onTouchEnd(event) {
        let distance = event.touches[0].clientX - this.state.startTouch
        if (distance < -50 || (distance > 0 && distance < 50)) {
            this.moveLeft()
        } else {
            this.moveRight()
        }
    }

    moveLeft() {
        let timer = setInterval(() => {
            let p = document.getElementsByTagName('ul')[0]
            p.style.left = p.offsetLeft - 5 + "px"
            console.log(p.offsetLeft)
            if (Math.abs(p.offsetLeft + 300) < 1 || Math.abs(p.offsetLeft) < 1 || Math.abs(p.offsetLeft + 600) < 1) {
                clearInterval(timer)
            }
        }, 10);
        this.state.srcs
    }

    moveRight(event) {
        
    }

    onTouchMove(event) {
        let touch = event.touches[0].clientX
        let p = event.target.parentNode.parentNode
        p.style.left = p.offsetLeft + touch - this.state.LastTouch + "px"
        this.setState({LastTouch:touch})
    }

    render() {
        return (
            <div className='App'>
                <ul className='Slide'
                    onTouchMove={this.onTouchMove}
                    onTouchStart={this.onTouchStart}
                    onTouchEnd={this.onTouchEnd}

                >
                    {
                        this.state['srcs'].map((src, index) => {
                            return <li key={index}><img src={"./img/img (" + src +").png"} alt=''/></li>
                        })
                    }
                </ul>
            </div>
        )
    }
}
