import React, { Component } from 'react'
import './css/reset.css'
import './App.css'

export default class App extends Component {

    constructor() {
        super()
        this.state = {
            srcs: [
                "./img/img (5).png",
                "./img/img (0).png",
                "./img/img (1).png"
            ]
        }
    }

    onTouchMove(event) {
        let touch = event.touches[0].clientX
        let offset = event.target.offsetLeft
        let p = event.target.parentNode.parentNode
        console.log(p)
        // console.log(touch.clientX)
        // console.log(event.target.style.offsetLeft)
        // console.log(window.getComputedStyle(event.target, null)['left'])
        // console.log(event.target.left)
        // console.log(document.getElementsByTagName('li')[0].offsetLeft)

        console.log(touch - offset)

        p.style.left = touch-offset+100
    }

    render() {
        return (
            <div className='App'>
                <ul className='Slide' onTouchMove={this.onTouchMove}>
                    {
                        this.state['srcs'].map((src, index) => {
                            return <li key={index}><img src={src} alt=''/></li>
                        })
                    }
                </ul>
            </div>
        )
    }
}
